function eventChangeDictType(){return function(){var n=$("#selectDictType").val();window.location.href=window.location.origin+`/Passage/${n}/1`}}function insertPassage(){var t=$("#ID").get(0).innerHTML,i=$("#selectDictType").get(0).value,r=$('[name="PassageType"]').get(0).value,u=$('[name="Source"]').get(0).value,f=$('[name="Destination"]').get(0).value,n=new FormData;isNumeric(t)?n.append("ID",t):n.append("ID",0);n.append("DictType",i);n.append("PassageType",r);n.append("Source",u);n.append("Destination",f);$.ajax({method:"POST",url:window.location.origin+"/api/InsertPassage",cache:!1,contentType:!1,processData:!1,data:n,success:function(){location.reload()},error:function(){loaderOff();showSnackbar("Lỗi: Đã xảy ra lỗi khi nhận yêu cầu chèn đoạn văn song ngữ này")}})}function updatePassage(n,t){$("#ID").get(0).innerHTML=$(`#ID${n}`).get(0).innerHTML;$('[name="PassageType"]').get(0).value=t;$('[name="Source"]').get(0).value=$("#source"+n).get(0).innerHTML;$('[name="Destination"]').get(0).value=$("#destination"+n).get(0).innerHTML;btnInsert.style.display="none";btnSave.style.display=""}function deletePassage(n){confirm("Bạn có muốn xóa đoạn văn song ngữ này không?")&&(loaderOn(),$.ajax({method:"GET",url:window.location.origin+"/api/DeletePassage/"+n,success:function(){location.reload()},error:function(){loaderOff();showSnackbar("Lỗi: Đã xảy ra lỗi khi nhận yêu cầu xoá đoạn văn song ngữ này")}}))}$(document).ready(function(){$("#selectDictType").change(eventChangeDictType())});